	return (
		<div className={css(classes.root)}>
			<div className={css(classes.homeContents)}>
				<img src={Svg} alt="" />
				<div className={css(classes.textContents)}>
					<div className={css(classes.name)}>Hey! I'm Vyshnav</div>
