						<button className={css(classes.Button2)}>HIRE ME</button>
					</div>
				</div>
				<div className={css(classes.imgContents)}>
					<img
						src={Developer}
						alt="Developer illustration"
						className={css(classes.developerImage)}
					/>
				</div>
			</div>
		</div>
	);
};

