import { StyleSheet, css } from "aphrodite";
import React from "react";
import Developer from "../../assets/images/developer.png";

const Home = () => {
	return (
		<div className={css(classes.root)}>
			<div className={css(classes.homeContents)}>
				<div className={css(classes.textContents)}>
					<div className={css(classes.name)}>Hey! I'm Vyshnav</div>
					<div className={css(classes.designation)}>FRONT-END DEVELOPER</div>
					<div className={css(classes.description)}>
						Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quam
						quisque porttitor diam, pellentesque mauris turpis ut ullamcorper.
						Quam dictum ut risus mi dolor ipsum ut facilisi sed. Purus, viverra
						integer pharetra mattis at semper at ut.
					</div>
					<div className={css(classes.connectOptn)}>
						<div>
							<span className={css(classes.email)}>Email</span>
							<br />
							<span>vyshnavks85@gmail.com</span>
						</div>
						<div>
							<span className={css(classes.email)}>Medium</span>
							<br />
							<span>medium.com/@vyshnavks85</span>
						</div>
					</div>
					<div className={css(classes.cvButton)}>
						<button className={css(classes.Button)}>DOWNLOAD CV</button>
					</div>
				</div>
				<div className={css(classes.imgContents)}>
					<img
						src={Developer}
						alt="Developer illustration"
						className={css(classes.developerImage)}
					/>
				</div>
			</div>
		</div>
	);
};

const classes = StyleSheet.create({
	root: {
		position: "relative",
		backgroundColor: "#1C1B1B",
		width: "100%",
		height: "100vh",
	},
	homeContents: {
		width: "66%",
		height: "55%",
		marginLeft: "18%",
		marginRight: "18%",
		display: "flex",
		flexDirection: "row",
	},

	textContents: {
		marginTop: 20,
		display: "flex",
		flexDirection: "column",
		maxWidth: "56%",
	},
	name: {
		fontWeight: 600,
		fontSize: 29,
		color: "#FFFFFF",
	},
	designation: {
		marginTop: 20,
		color: "#00ECEC",
		fontSize: 58,
		fontWeight: "bold",
	},
	description: {
		marginTop: 30,
		color: "#99A5A6",
		fontSize: 24,
		fontWeight: "normal",
	},
	connectOptn: {
		marginTop: 65,
		fontSize: 19,
		fontWeight: "normal",
		color: "#9F9F9F",
		display: "flex",
		flexDirection: "row",
		justifyContent: "space-between",
	},
	email: {
		color: "#DDD5D5",
		fontSize: 24,
		fontWeight: 600,
	},
	cvButton: {
		marginTop: "8%",
	},
	Button: {
		backgroundColor: "#00ECEC",
		color: "black",
		borderRadius: 8,
		width: 260,
		height: 58,
		fontSize: 20,
		fontWeight: 500,
	},
	imgContents: {
		maxWidth: "44%",
	},
	developerImage: {
		width: "100%",
		height: "7%",
		marginTop: "15%",
	},
});

export default Home;
