import React, { useState } from "react";
import { StyleSheet, css } from "aphrodite";
import { useHistory } from "react-router";

const Header = () => {
	const [value, setValue] = useState(0);
	const history = useHistory();

	setValue((prV) => {

		if (value === 0) {
			history.push("/");
		}
		if (value === 1) {
			history.push("/works");
		}
		if (value === 2) {
			history.push("/services");
		}
		if (value === 3) {
			history.push("/contact");
		}
		if (value === 4) {
			history.push("/about");
		}
	});

	return (
		<div className={css(classes.header)}>
			<div className={css(classes.headerComp)} value={0} onClick={setValue}>
				HOME
			</div>
			<div className={css(classes.headerComp)} value={1} onClick={setValue}>
				WORKS
			</div>
			<div className={css(classes.headerComp)} value={2} onClick={setValue}>
				SERVICES
			</div>
			<div className={css(classes.headerComp)} value={3} onClick={setValue}>
				CONTACT
			</div>
			<div className={css(classes.headerComp)} value={4} onClick={setValue}>
				ABOUT
			</div>
		</div>
	);
};

const classes = StyleSheet.create({
	header: {
		display: "flex",
		flexDirection: "row",
		justifyContent: "space-evenly",
		color: "#585858",
		backgroundColor: "#1C1B1B",
		padding: 70,
		paddingLeft: "37%",
		paddingRight: "37%",

		// position: 'absolute',
	},
	headerComp: {
		fontWeight: 500,
		fontSize: 18,
	},
});

export default Header;
