import React from "react";
import { StyleSheet, css } from "aphrodite";
import Header from "../components/Header/Header";
import { COLORS } from "../styles/constants";

const Works = () => {
	return (
		<div className={css(classes.root)}>
			<Header />
			<div className={css(classes.container)}>
				<span className={css(classes.title)}>PORTFOLIO</span>
				<div className={css(classes.cards)}>
					<div className={css(classes.card_r1)}>
						<div className={css(classes.card)}>
							<span className={css(classes.card_title)}>APP NAME</span>
							<span className={css(classes.card_desc)}>
								Lorem ipsum dolor sit amet, consectetur adipiscing elit. Laoreet
								lectus amet egestas feugiat
							</span>
							<button className={css(classes.card_button)}>KNOW MORE</button>
						</div>
					</div>
					<div className={css(classes.card_r2)}></div>
				</div>
				<button className={css(classes.button)}>MORE PROJECTS</button>
			</div>
		</div>
	);
};

const classes = StyleSheet.create({
	root: {
		display: "flex",
		flexDirection: "column",
	},
	container: {
		display: "flex",
		flexDirection: "column",
		textAlign: "center",
		marginLeft: "auto",
		marginRight: "auto",
		maxWidth: 1241,
		maxHeight: 420,
	},
	title: {
		textAlign: "left",
		fontWeight: "bold",
		fontSize: 40,
		lineHeight: "60px",
		letterSpacing: "0.02em",
		color: COLORS.secondary,
	},
	cards: {
		display: "flex",
		flexDirection: "column",
	},
	card_r1: {
		display: "flex",
		flexDirection: "row",
		justifyContent: "space-between",
	},
	card: {
		width: 404,
		height: 178,
		backgroundColor: COLORS.secondary_shade,
		borderRadius: 8,
		display: "flex",
		flexDirection: "column",
		textAlign: "left",
		padding: "12px 18px",
	},
	card_title: {
		fontSize: 22,
		lineHeight: "33px",
		fontWeight: 600,
		color: COLORS.secondary,
	},
	card_desc: {
		fontSize: 17,
		lineHeight: "25px",
		color: COLORS.white,
	},
});

export default Works;
