import React, { useState } from "react";
import { StyleSheet, css } from "aphrodite";
import { Link } from "react-router-dom";

const Header = () => {
	// const [values, setValue] = useState("#585858");
	// const [navfont, setNavfont] = useState(500);

	// const Navclick = () => {
	// 	setValue("#FFFFFF");
	// 	setNavfont(700);
	// };
	// let linkColor = "#585858";

	const classes = StyleSheet.create({
		header: {
			display: "flex",
			flexDirection: "row",
			justifyContent: "space-evenly",
			//color: "#585858",
			backgroundColor: "#1C1B1B",
			padding: 70,
			// paddingLeft: "37%",
			// paddingRight: "37%",
			textDecoration: "none",
			minWidth: 480,

			// position: 'absolute',
		},
		headerComp: {
			fontWeight: 500,
			fontSize: 18,
			textDecoration: "none",
			color: "#585858",
			":hover": {
				color: "#FFFFFF",
				fontWeight: 700,
			},
		},
	});

	return (
		<div className={css(classes.header)}>
			<div className={css(classes)}></div>
		</div>
	);
};

export default Header;
