import React, { useState } from "react";
import { StyleSheet, css } from "aphrodite";
import { Link } from "react-router-dom";

const Header = () => {
	// const [values, setValue] = useState("#585858");
	// const [navfont, setNavfont] = useState(500);

	// const Navclick = () => {
	// 	setValue("#FFFFFF");
	// 	setNavfont(700);
	// };
	// let linkColor = "#585858";

	const classes = StyleSheet.create({
		header: {
			//color: "#585858",
			backgroundColor: "#1C1B1B",
			padding: 70,
			textDecoration: "none",
			display: 'flex',
			justifyContent: 'center',


			// position: 'absolute',
		},
		headerComp: {
			fontWeight: 500,
			fontSize: 18,
			textDecoration: "none",
			color: "#585858",
			":hover": {
				color: "#FFFFFF",
				fontWeight: 700,
			},
		},
		headerList: {
			display: "flex",
			flexDirection: "row",
			justifyContent: "space-evenly",
			// marginLeft: "35%",
			// marginRight: "35%",
			minWidth: 480,
		},
	});

	return (
		<div className={css(classes.header)}>
			<div className={css(classes.headerList)}>
				<Link className={css(classes.headerComp)} value="home" to="/">
					HOME
				</Link>
				<Link
					className={css(classes.headerComp)}
					value="works"
					to="/works"
					// onClick={Navclick}
				>
					WORKS
				</Link>
				<Link
					className={css(classes.headerComp)}
					value="services"
					to="/services"
				>
					SERVICES
				</Link>
				<Link className={css(classes.headerComp)} vlaue="contact" to="/contact">
					CONTACT
				</Link>
				<Link className={css(classes.headerComp)} value="about" to="/about">
					ABOUT
				</Link>
			</div>
		</div>
	);
};

export default Header;
