import React from "react";
import { BrowserRouter, Route } from "react-router-dom";
import Home from "./pages/Desktop/Home";
import "./App.css";
import { Switch } from "react-router";
import About from "./pages/Desktop/About";
import Works from "./pages/Desktop/Works";
import Services from "./pages/Desktop/Services";
import Contact from "./pages/Desktop/Contact";
import HomeMob from "./pages/Mobile/HomeMob";
import useDeviceType from "./hooks/deviceType";

function App() {
	const deviceType = useDeviceType();
	const checking = () => {
		if (path === "/") {
			if (deviceType === "mobile") {
				return HomeMob;
			} else return Home;
		}
	};

	return (
		<BrowserRouter>
			<div className="app">
				<Switch>
					<Route path="/" component={checking()} exact></Route>
					<Route path="/works" component={Works}></Route>
					<Route path="/services" component={Services}></Route>
					<Route path="/contact" component={Contact}></Route>
					<Route path="/about" component={About}></Route>
				</Switch>
			</div>
		</BrowserRouter>
	);
}

export default App;
