import React, { useState } from "react";
import { StyleSheet, css } from "aphrodite";
import { Link } from "react-router-dom";

const Header = () => {
	const [values, setValue] = useState("#585858");
	const [navfont, setNavfont] = useState(500);

	const Navclick = () => {
		setValue("#FFFFFF");
		setNavfont(700);
	};

	const classes = StyleSheet.create({
		header: {
			display: "flex",
			flexDirection: "row",
			justifyContent: "space-evenly",
			//color: "#585858",
			backgroundColor: "#1C1B1B",
			padding: 70,
			paddingLeft: "37%",
			paddingRight: "37%",
			textDecoration: "none",

			// position: 'absolute',
		},
		headerComp: {
			fontWeight: navfont,
			fontSize: 18,
			textDecoration: "none",
			color: values,
		},
	});

	return (
		<div className={css(classes.header)}>
			<Link className={css(classes.headerComp)} value="home" to="/">
				HOME
			</Link>
			<Link
				className={css(classes.headerComp)}
				value="works"
				to="/works"
				onClick={Navclick}
			>
				WORKS
			</Link>
			<Link className={css(classes.headerComp)} value="services" to="/services">
				SERVICES
			</Link>
			<Link className={css(classes.headerComp)} vlaue="contact" to="/contact">
				CONTACT
			</Link>
			<Link className={css(classes.headerComp)} value="about" to="/about">
				ABOUT
			</Link>
		</div>
	);
};

export default Header;
