import React, { useState } from "react";
import { StyleSheet, css } from "aphrodite";
import { useHistory } from "react-router";

const Header = () => {
	const history = useHistory();

	return (
		<div className={css(classes.header)}>
			<div
				className={css(classes.headerComp)}
				value={0}
				onClick={() => {
					history.push("/");
				}}
			>
				HOME
			</div>
			<div
				className={css(classes.headerComp)}
				value={1}
				onClick={() => {
					history.push("/");
				}}
			>
				WORKS
			</div>
			<div
				className={css(classes.headerComp)}
				value={2}
				onClick={() => {
					history.push("/");
				}}
			>
				SERVICES
			</div>
			<div
				className={css(classes.headerComp)}
				value={3}
				onClick={() => {
					history.push("/");
				}}
			>
				CONTACT
			</div>
			<div
				className={css(classes.headerComp)}
				value={4}
				onClick={() => {
					history.push("/");
				}}
			>
				ABOUT
			</div>
		</div>
	);
};

const classes = StyleSheet.create({
	header: {
		display: "flex",
		flexDirection: "row",
		justifyContent: "space-evenly",
		color: "#585858",
		backgroundColor: "#1C1B1B",
		padding: 70,
		paddingLeft: "37%",
		paddingRight: "37%",

		// position: 'absolute',
	},
	headerComp: {
		fontWeight: 500,
		fontSize: 18,
	},
});

export default Header;
