import { css, StyleSheet } from "aphrodite";
import React, { useState } from "react";
import Menu from "../../assets/icons/menubar.png";
import MenuCross from "../../assets/icons/menubarCross.png";

const NavBar = () => {
	const [sidebar, setSidebar] = useState(false);
	const showSidebar = () => setSidebar(!sidebar);
	return (
		<div className={css(styles.root)}>
			<div className={css(styles.navMenuBar)}>
				<img src={Menu} alt="" className={css(styles.menuBar)} />
				<div className={css(styles.rootNav)}>
					<nav
						className={
							sidebar ? css(styles.navMenuActive) : css(styles.navMenu)
						}
					>
						<div className={css(styles.menuBarCross)}>
							<img src={MenuCross} alt="" className={css(styles.cross)} />
						</div>
					</nav>
				</div>
			</div>
		</div>
	);
};

const styles = StyleSheet.create({
	root: {
		display: "flex",
		justifyContent: "flex-end",
	},
	navMenuBar: {
		margin: 20,
	},
	navMenu: {
		overflowY: "hidden",
		top: 0,
		height: "100vh",
		width: "100%",
		right: "-100%",
		position: "fixed",
		transition: "450ms",
	},
	navMenuActive: {
		top: 0,
		zIndex: 1,
		right: 0,
		transition: "350ms",
		height: "100vh",
		width: "100%",
		position: "fixed",
		backgroundColor: "#00ECEC",
	},
});

export default NavBar;
