import { StyleSheet, css } from "aphrodite";
import React from "react";
import Header from "../../components/Header/Header";
import Developer from "../../assets/images/wdev.png";
import { COLORS } from "../../styles/constants";

const Home = () => {
	return (
		<div className={css(classes.root)}>
			<Header />
			<div className={css(classes.container)}>
				<div className={css(classes.leftContainer)}>
					<span className={css(classes.name)}>Hey! I'm Vyshnav</span>
					<span className={css(classes.title)}>FRONT-END DEVELOPER</span>
					<span className={css(classes.description)}>
						Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quam
						quisque porttitor diam, pellentesque mauris turpis
					</span>
					<button className={css(classes.button)}>DOWNLOAD CV</button>
				</div>
				<div className={css(classes.image)}>
					<img
						src={Developer}
						alt="Developer"
						className={css(classes.developer)}
					/>
				</div>
			</div>
		</div>
	);
};

const classes = StyleSheet.create({
	root: {
		display: "flex",
		flexDirection: "column",
	},
	container: {
		display: "flex",
		flexDirection: "row",
		marginLeft: "auto",
		marginRight: "auto",
		maxWidth: 1241,
		maxHeight: 420,
		justifyContent: "center",
		alignItems: "center",
		marginTop: 200,
		"@media (max-width: 1536px)": {
			maxWidth: 993,
			maxHeight: 336,
			marginTop: 160,
		},
		"@media (max-width: 1280px)": {
			maxWidth: 827,
			maxHeight: 280,
			marginTop: 133,
		},
	},
	leftContainer: {
		display: "flex",
		flexDirection: "column",
		textAlign: "left",
		maxWidth: 714,
		"@media (max-width: 1536px)": {
			maxWidth: 571,
		},
		"@media (max-width: 1280px)": {
			maxWidth: 476,
		},
	},
	name: {
		marginBottom: 20,
		fontSize: 29,
		lineHeight: "43px",
		fontWeight: 600,
		color: COLORS.white,
		"@media (max-width: 1536px)": {
			marginBottom: 16,
			fontSize: 23,
			lineHeight: "34px",
		},
		"@media (max-width: 1280px)": {
			marginBottom: 13,
			fontSize: 19,
			lineHeight: "29px",
		},
	},
	title: {
		lineHeight: "87px",
		marginBottom: 20,
		letterSpacing: "0.02em",
		fontSize: 58,
		fontWeight: "bold",
		color: COLORS.secondary,
		"@media (max-width: 1536px)": {
			lineHeight: "70px",
			marginBottom: 16,
			letterSpacing: "0.02em",
			fontSize: 46,
		},
		"@media (max-width: 1280px)": {
			lineHeight: "58px",
			marginBottom: 13,
			letterSpacing: "0.02em",
			fontSize: 39,
		},
	},
	description: {
		fontSize: 28,
		marginBottom: 45,
		lineHeight: "43px",
		color: COLORS.white,
		"@media (max-width: 1536px)": {
			fontSize: 22.4,
			marginBottom: 36,
			lineHeight: "34px",
		},
		"@media (max-width: 1280px)": {
			fontSize: 19,
			marginBottom: 30,
			lineHeight: "30px",
		},
	},
	button: {
		width: 264,
		height: 60,
		backgroundColor: COLORS.primary,
		color: COLORS.white,
		border: `1px solid ${COLORS.secondary}`,
		borderRadius: 8,
		fontSize: 20,
		fontWeight: 500,
		lineHeight: "30px",
		letterSpacing: "0.0125em",
		cursor: "pointer",
		"@media (max-width: 1536px)": {},
		"@media (max-width: 1280px)": {},
	},
	image: {
		marginLeft: 20,
		"@media (max-width: 1536px)": {},
		"@media (max-width: 1280px)": {},
	},

	developer: {},
});

export default Home;
