import React, { useState, useEffect, useHistory } from "react";
import { StyleSheet, css } from "aphrodite";

const Header = () => {
	const [value, setValue] = useState(0);
	const history = useHistory();

	setValue(() => {
		if (value === 0) history.push("/");
		if (value === 1) history.push("/works");
		if (value === 2) history.push("/services");
		if (value === 3) history.push("/contact");
		if (value === 4) history.push("/about");
	});

	return (
		<>
		</>
	);
};

const classes = StyleSheet.create({
	header: {
		display: "flex",
		flexDirection: "row",
		justifyContent: "space-evenly",
		color: "#585858",
		backgroundColor: "#1C1B1B",
		padding: 70,
		paddingLeft: "37%",
		paddingRight: "37%",

		// position: 'absolute',
	},
	headerComp: {
		fontWeight: 500,
		fontSize: 18,
	},
});

export default Header;
