import React, { useState, useEffect, useHistory } from "react";
import { StyleSheet, css } from "aphrodite";

const Header = () => {
	const [value, setValue] = useState(0);
	const history = useHistory();

	useEffect(()=>{
		if(value === 0)
			history.push("/");
		if(value === 0)
			history.push("/works");
		if(value === 0)
			history.push("/services");
		if(value === 0)
			history.push("/contact");
		if(value === 4)
			history.push("/about");
	},[value, history])

	return (
		<div className={css(classes.header)} onChange ={(event,newValue)=>{
			setValue(newValue);
		}}>
			<div className={css(classes.headerComp)} value={0}>
				HOME
			</div>
			<div className={css(classes.headerComp)} value={1}>
				WORKS
			</div>
			<div className={css(classes.headerComp)} value={2}>
				SERVICES
			</div>
			<div className={css(classes.headerComp)} value={3}>
				CONTACT
			</div>
			<div className={css(classes.headerComp)} value={4}>
				ABOUT
			</div>
		</div>
	);
};

const classes = StyleSheet.create({
	header: {
		display: "flex",
		flexDirection: "row",
		justifyContent: "space-evenly",
		color: "#585858",
		backgroundColor: "#1C1B1B",
		padding: 70,
		paddingLeft: "37%",
		paddingRight: "37%",

		// position: 'absolute',
	},
	headerComp: {
		fontWeight: 500,
		fontSize: 18,
	},
});

export default Header;
