const HomeMobile = () => {
	return (
		<div className={css(styles.root)}>
			<div className={css(styles.container)}>
				<div className={css(styles.name)}>Hey! Iâ€™m Vyshnav</div>
				<div className={css(styles.title)}>FRONT-END DEVELOPER</div>
				<div className={css(styles.image)}>
					<img src={Dev} alt="" className={css(styles.dev)} />
				</div>
				<div className={css(styles.description)}>
					I like to craft solid and scalable frontend products with great user
					experiences.
				</div>
				<button
					className={css(styles.button)}
					onTouchStart={() => {
						window.open(
							"https://drive.google.com/file/d/1eqYQHKm5lHeK3iwiW1l5kSIHLpZdmoB4/view?usp=sharing",
							"_blank"
						);
					}}
				>
					{" "}
					DOWNLOAD CV{" "}
				</button>
			</div>
		</div>
	);
};
const styles = StyleSheet.create({
	root: {
		alignItems: "center",
		marginLeft: 20,
		marginRight: 20,
	},
	container: {
		display: "flex",
		flexDirection: "column",
		width: "100%",
	},
	name: {
		color: COLORS.white,
		fontSize: 18,
		lineHeight: "27px",
		marginTop: 28,
		fontWeight: 600,
	},
	title: {
		marginTop: 10,
		fontSize: 27,
		lineHeight: "42px",
		// letterSpacing: ".02em",
		fontWeight: "bold",
		color: COLORS.secondary,
	},
	image: {
		textAlign: "center",
		alignItems: "center",
