	dev: {
		marginTop: 20,
		maxWidth: 314,
		maxHeight: 314,
	},
	description: {
		marginTop: 30,
		fontSize: 18,
		lineHeight: "26px",
		color: COLORS.white,
	},
	button: {
		background: "transparent",
		border: `1px solid ${COLORS.secondary}`,
		width: 154,
		height: 40,
		borderRadius: 8,
		fontSize: 13,
		marginTop: 24,
		marginBottom: 24,
		fontWeight: 500,
		textAlign: "center",
		alignItems: "center",
		color: COLORS.white,
	},
});

export default HomeMobile;
